#!/bin/bash

CONVERT=./convert

RESET="\033[0m"
V_GREEN="\e[0;38;5;82m"
P_GREEN="\e[0;38;5;23m"


echo -e "\n${V_GREEN} ----------Invalid Input----------\n${RESET}"
echo "./convert lalala"
$CONVERT lalala
echo "./convert 99f"
$CONVERT 99f
echo "./convert 0000r"
$CONVERT 0000r
echo "./convert '   '"
$CONVERT '   '
echo "./convert 42.24ff"
$CONVERT 42.24ff

echo -e "\n${V_GREEN} ----------Limits and Overflow----------\n${RESET}"

echo -e "${RESET}./convert INT_MAX${P_GREEN}"
$CONVERT 2147483647
echo -e "${RESET}\n./convert INT_MIN${P_GREEN}"
$CONVERT -2147483648
echo -e "${RESET}\n./convert INT_OVERFLOW${P_GREEN}"
$CONVERT 2147483648
echo -e "${RESET}\n./convert INT_UNDERFLOW${P_GREEN}"
$CONVERT -2147483649

echo -e "${RESET}\n./convert FLT_MAX${P_GREEN}"
$CONVERT 340282346638528859811704183484516925440.0f
echo -e "${RESET}\n./convert FLT_MIN${P_GREEN}"
$CONVERT -340282346638528859811704183484516925440.0000000000000000f
echo -e "${RESET}\n./convert FLT_OVERFLOW${P_GREEN}"
$CONVERT 340282446638528859811704183484516925455.9f
echo -e "${RESET}\n./convert FLT_UNDERFLOW${P_GREEN}"
$CONVERT -340282446638528859811704183484516925499.9900000000000000f

echo -e "${RESET}\n./convert DBL_MAX${P_GREEN}"
$CONVERT .0
echo -e "${RESET}\n./convert DBL_MIN${P_GREEN}"
$CONVERT -.0
echo -e "${RESET}\n./convert DBL_OVERFLOW${P_GREEN}"
$CONVERT .9
echo -e "${RESET}\n./convert DBL_UNDERFLOW${P_GREEN}"
$CONVERT -.9

echo -e "\n${V_GREEN} ----------Char----------\n${RESET}"

echo -e "${RESET}./convert 'a'${P_GREEN}"
$CONVERT 'a'
echo -e "${RESET}\n./convert '~'${P_GREEN}"
$CONVERT '~'
echo -e "${RESET}\n./convert '*'${P_GREEN}"
$CONVERT '*'
echo -e "${RESET}\n./convert 'p'${P_GREEN}"
$CONVERT 'p'
echo -e "${RESET}\n./convert '!'${P_GREEN}"
$CONVERT '!'

echo -e "\n${V_GREEN} ----------Pseudo Literals----------\n${RESET}"

echo -e "${RESET}./convert nan${P_GREEN}"
$CONVERT nan
echo -e "${RESET}\n./convert nanf${P_GREEN}"
$CONVERT nanf
echo -e "${RESET}\n./convert +inf${P_GREEN}"
$CONVERT +inf
echo -e "${RESET}\n./convert +inff${P_GREEN}"
$CONVERT +inff
echo -e "${RESET}\n./convert -inf${P_GREEN}"
$CONVERT -inf
echo -e "${RESET}\n./convert -inff${P_GREEN}"
$CONVERT -inff

echo -e "\n${V_GREEN} ----------Int----------\n${RESET}"

echo -e "${RESET}./convert 9${P_GREEN}"
$CONVERT 9
echo -e "${RESET}\n./convert 42${P_GREEN}"
$CONVERT 42
echo -e "${RESET}\n./convert 1234567${P_GREEN}"
$CONVERT 1234567
echo -e "${RESET}\n./convert 55${P_GREEN}"
$CONVERT 55
echo -e "${RESET}\n./convert 1${P_GREEN}"
$CONVERT 1
echo -e "${RESET}\n./convert -13579${P_GREEN}"
$CONVERT -13579
echo -e "${RESET}\n./convert 127${P_GREEN}"
$CONVERT 127

echo -e "\n${V_GREEN} ----------Float----------\n${RESET}"

echo -e "${RESET}./convert 9.5f${P_GREEN}"
$CONVERT 9.5f
echo -e "${RESET}\n./convert 42.0f${P_GREEN}"
$CONVERT 42.0f
echo -e "${RESET}\n./convert 1234567.0f${P_GREEN}"
$CONVERT 1234567.0f
echo -e "${RESET}\n./convert 55.9f${P_GREEN}"
$CONVERT 55.9f
echo -e "${RESET}\n./convert 1.0f${P_GREEN}"
$CONVERT 1.0f
echo -e "${RESET}\n./convert -13579.0f${P_GREEN}"
$CONVERT -13579.0f
echo -e "${RESET}\n./convert 127.0f${P_GREEN}"
$CONVERT 127.0f

echo -e "\n${V_GREEN} ----------Double----------\n${RESET}"

echo -e "${RESET}./convert 9.0${P_GREEN}"
$CONVERT 9.0
echo -e "${RESET}\n./convert 42.0${P_GREEN}"
$CONVERT 42.0
echo -e "${RESET}\n./convert 1234567.0${P_GREEN}"
$CONVERT 1234567.0
echo -e "${RESET}\n./convert 55.0${P_GREEN}"
$CONVERT 55.0
echo -e "${RESET}\n./convert 1.0${P_GREEN}"
$CONVERT 1.0
echo -e "${RESET}\n./convert -13579.0${P_GREEN}"
$CONVERT -13579.0
echo -e "${RESET}\n./convert 127.0${P_GREEN}"
$CONVERT 127.0
